@if($errors->any())
  <div class="ui red message">
    <div class="header">
      An unexpected error has occured
    </div>
    <ul class="list">
    @foreach($errors->all() as $error)
      <li><i class="fas fa-exclamation-triangle"></i> <b>{{ $error }}<b></li>
    @endforeach
    </ul>
  </div>
@endif

@if (\Session::has('success'))
    <div class="ui green message">
      <div class="header">
        Success!
      </div>
      <ul class="list">
          <li>{!! \Session::get('success') !!}</li>
      </ul>
    </div>
@endif


